class Solution {
public:
    bool canPartition(vector<int>& nums) {
        int sum = 0;
        int target;
        vector<int> dp(10001,0);
        for(int i = 0; i<nums.size(); i++){
            sum += nums[i];
        }
        target = sum/2;
        for (int i = 0; i < nums.size(); i++){
            for(int j = target; j >=nums[i]; j--){
                dp[j] = max(dp[j], dp[j-nums[i]]+nums[i]); 
            }
        }
        if (dp[target]==target) return true;
        return false;
    }
};
```

